import React from 'react';
import { FormContext } from '../FormProvider';

const ContactForm = (props) =>  {

  console.log('propz', props);

  const updateField = (dispatch, e) => {
    dispatch({
      type: e.target.id,
      payload: {
        key: e.target.id,
        value: e.target.value
      }
    });
  }

  return (
    <FormContext.Consumer>
      { ({dispatch, state}) =>
        <div>
          <input id="fullName" type='text' placeholder='Full Name' onChange={e => updateField(dispatch, e)} value={state.fullName} />
          <input id="phone" type='phone' placeholder='8888888888' onChange={e => updateField(dispatch, e)} value={state.phone} />
          <input id="email" type='email' placeholder='placeholder@email.com' onChange={e => updateField(dispatch, e)} value={state.email} />
        </div>
      }
    </FormContext.Consumer>
  );
}

export default ContactForm;
