import React, { useState } from 'react';
import ContactForm from './contact';
import FormProvider from './FormProvider';

const FormWrapper = () => {
  // state for each attribute
  const [currentPage, setCurrentPage] = useState(0);

  // all form components
  const forms = [
    <ContactForm />,
  ]

  // handle paging between forms
  const hasNextPage = currentPage < forms.length - 1;
  const hasLastPage = currentPage > 0;
  const nextPage = () => {
    if (hasNextPage) {
      setCurrentPage(currentPage + 1);

    }
  }
  const lastPage = () => {
    if (hasLastPage) {
      setCurrentPage(currentPage - 1)
    }
  }

  // render 1 form at a time
  return (
    <FormProvider>
      {forms[currentPage]}
      <button disabled={!hasLastPage} onClick={lastPage}>last</button>
      <button disabled={!hasNextPage} onClick={nextPage}>next</button>
    </FormProvider>
  );
}

export default FormWrapper;
