import React, { useState } from 'react';
import ContactForm from './contact';

const FormWrapper = () => {
  // state for each attribute
  const [currentPage, setCurrentPage] = useState(0);
  const [fullName, setFullName] = useState('');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');

  // simulate post body from multiple forms
  const submitFullForm = () => {
    console.log('post body', {
      fullName,
      phone,
      email
    })
  }

  // contact form
  const submitContactForm = (event, fullName, phone, email) => {
    event.preventDefault();

    setFullName(fullName);
    setPhone(phone);
    setEmail(email);
  }

  // all form components
  const forms = [
    <ContactForm onSubmit={submitContactForm} />
  ]

  // handle paging between forms
  const hasNextPage = currentPage < forms.length - 1;
  const hasLastPage = currentPage > 0;
  const nextPage = () => {
    if (hasNextPage) {
      setCurrentPage(currentPage + 1);
    }
  }
  const lastPage = () => {
    if (hasLastPage) {
      setCurrentPage(currentPage - 1)
    }
  }

  // render 1 form at a time
  return <div>
    {forms[currentPage]}
    <button disabled={!hasLastPage} onClick={lastPage}>last</button>
    <button disabled={!hasNextPage} onClick={nextPage}>next</button>
    {!hasNextPage && <button onClick={submitFullForm}>submit all</button>}
  </div>
}

export default FormWrapper;
