import React, { useRef } from 'react';

const ContactForm = (props) =>  {
  const { dispatch, pageNumber } = props;
  const fullNameRef = useRef(null);
  const phoneRef = useRef(null);
  const emailRef = useRef(null);

  const updateField = (dispatch, e) => {
    dispatch({
      type: e.target.id,
      payload: {
        key: e.target.id,
        value: e.target.value
      }
    });
  }

  // simple validation
  console.log('uhhh', Date.now())
  if (fullNameRef && phoneRef && emailRef) {
    if (fullNameRef.current.value && phoneRef.current.value && emailRef.current.value) {
      dispatch({
        type: 'validation',
        payload: {
          pageNumber,
          valid: true
        }
      })
    } else {
      dispatch({
        type: 'validation',
        payload: {
          pageNumber,
          valid: false
        }
      })
    }
  }

  return (
    <div>
      <input
        id="fullName"
        type='text'
        placeholder='Full Name'
        onChange={e => updateField(dispatch, e)}
        ref={fullNameRef}
      />
      <input
        id="phone"
        type='phone'
        placeholder='8888888888'
        onChange={e => updateField(dispatch, e)}
        ref={phoneRef}
      />
      <input
        id="email"
        type='email'
        placeholder='placeholder@email.com'
        onChange={e => updateField(dispatch, e)}
        ref={emailRef}
      />
    </div>
  );
}

export default ContactForm;
