import React, { useState } from 'react';
import ContactForm from './contact';

const FormWrapper = () => {
  const [currentPage, setCurrentPage] = useState(0);
  const [fullName, setFullName] = useState('');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');

  const hasNextPage = currentPage < forms.length - 1;
  const hasLastPage = currentPage > 0;

  const submitFullForm = () => {
    console.log('post body', {
      fullName,
      phone,
      email
    })
  }

  const submitContactForm = (fullName, phone, email) => {
    setFullName(fullName);
    setPhone(phone);
    setEmail(email);
  }

  const forms = [
    <ContactForm onSubmit={submitContactForm} />
  ]

  const nextPage = () => {
    if (hasNextPage) {
      setCurrentPage(currentPage + 1);
    }
  }

  const lastPage = () => {
    if (hasLastPage) {
      setCurrentPage(currentPage - 1)
    }
  }

  return <div>
    {forms[currentPage]}
    <button disabled={!hasLastPage} onClick={lastPage}>last</button>
    <button disabled={!hasNextPage} onClick={nextPage}>next</button>
    {!hasNextPage && <button onClick={submitFullForm}>submit all</button>}
  </div>
}

export default FormWrapper;
