import React, { useEffect } from 'react';

const ContactForm = (props) =>  {

  const { dispatch, state } = props;

  useEffect(() => {console.log('fullName')}, [state])
  useEffect(() => {console.log('phone')}, [state.phone])
  useEffect(() => {console.log('email')}, [state.email])

  const updateField = (dispatch, e) => {
    dispatch({
      type: e.target.id,
      payload: {
        key: e.target.id,
        value: e.target.value
      }
    });
  }

  return (
    <div>
      <input id="fullName" type='text' placeholder='Full Name' onChange={e => updateField(dispatch, e)} />
      <input id="phone" type='phone' placeholder='8888888888' onChange={e => updateField(dispatch, e)} />
      <input id="email" type='email' placeholder='placeholder@email.com' onChange={e => updateField(dispatch, e)} />
    </div>
  );
}

export default ContactForm;
