import React, { useReducer, useState } from 'react';
import ContactForm from './contact';

const initialState = {
  fullName: 'jimbo'
};

const formsReducer = (state, action) => {
  switch (action.type) {
    case 'contactForm':
      return {
        ...state,
        fullName: action.payload.fullName,
        phone: action.payload.phone,
        email: action.payload.email
      };
    default:
      return state;
  }
};

const [state, dispatch] = useReducer(
  formsReducer,
  initialState,
);

const FormWrapper = () => {
  const forms = [ContactForm]
  const [currentPage, setCurrentPage] = useState(0);

  const hasNextPage = currentPage < forms.length - 1;
  const hasLastPage = currentPage > 0;

  const nextPage = () => {
    if (hasNextPage) {
      setCurrentPage(currentPage + 1);
    }
  }

  const lastPage = () => {
    if (hasLastPage) {
      setCurrentPage(currentPage - 1)
    }
  }

  return <div>
    {forms[currentPage]}
    <button active={hasLastPage} onClick={lastPage}>last</button>
    <button active={hasNextPage} onClick={nextPage}>next</button>
    {!hasNextPage && <button onClick={() => console.log('submit')}>submit all</button>}
  </div>
}

export default FormWrapper;
